//front end encryption, server side will have its own encryption of data 
// stored that way if database is hacked passwords are safe

//https://stackoverflow.com/questions/105034/how-to-create-a-guid-uuid
function uuidv4() {
    return ([1e7]+-1e3+-4e3+-8e3+-1e11).replace(/[018]/g, c =>
      (c ^ crypto.getRandomValues(new Uint8Array(1))[0] & 15 >> c / 4).toString(16)
    );
  }
//takes a password and a salt which will be the uuidv4 generated by the above function
//this will require when logging in requesting user salt from server to encrypt the password
function encryptPB(password, salt) {
    return CryptoJS.PBKDF2(password,salt,{keySize:512/32, iterations:10000});
}


var passPhrase = "This is the secret pasphrase to protect passwords sent over internet."

function encryptAES(password) {
    return CryptoJS.AES.encrypt(password, passPhrase);
}

function decryptAES(encrypted) {
    return CryptoJS.AES.decrypt(encrypted,passPhrase);
}